import React from "react";
import { useFormik } from "formik";
import { useForm, ValidationError } from '@formspree/react';
import {
  Box,
  Button,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Input,
  Select,
  Textarea,
  VStack,
} from "@chakra-ui/react";
import * as Yup from "yup";
import FullScreenSection from "./FullScreenSection";
import useSubmit from "../hooks/useSubmit";
import { useAlertContext } from "../context/alertContext";

const LandingSection = () => {
  const { isLoading, response, submit } = useSubmit();
  const { onOpen } = useAlertContext();

  const style = { backgroundColor: "#512DA8" }
  let sentMail=()=>{
    [state, handleSubmit1] = useForm("xyyaqrde");
    const formik = useFormik({
      initialValues: {
        firstName: "",
        email: "",
        type: "",
        comment: "",
      },
      onSubmit: (values) => {
          submit(values)
            .then(() => {
              onOpen(
                "success",
                `Thank you, ${values.firstName}! Your form has been submitted successfully.`
              );

              formik.resetForm();
            })
            .catch((error) => {
              console.error(error);
              onOpen(
                "error",
                "There was an error submitting the form. Please try again."
              );
            });

        },

      validationSchema: Yup.object({
        firstName: Yup.string().required("Required"),
        email: Yup.string().required("Required").email("Invalid email address"),
        comment: Yup.string()
          .required("Required")
          .min(25, "Must be at least 25 characters"),
      }),
    });


    const { handleSubmit, handleChange, handleBlur, values, touched, errors } =
      formik;
  } 





  return (
    <FullScreenSection
      isDarkBackground
      backgroundColor="#512DA8"
      py={16}
      spacing={8}
    >
      <VStack w="1024px" p={32} alignItems="flex-start">
        <Heading as="h1" id="contactme-section">
          Contact me
        </Heading>
        <Box p={6} rounded="md" w="100%">
          <form onSubmit={sentMail} >
            <VStack spacing={4}>
              <FormControl isInvalid={touched.firstName && errors.firstName}>
                <FormLabel htmlFor="firstName">Name</FormLabel>
                <Input
                  id="firstName"
                  name="firstName"
                  value={values.firstName}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                <FormErrorMessage>{errors.firstName}</FormErrorMessage>
              </FormControl>
              <FormControl isInvalid={touched.email && errors.email}>
                <FormLabel htmlFor="email">Email Address</FormLabel>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  value={values.email}
                  onChange={handleChange}
                  onBlur={handleBlur}
                />
                <FormErrorMessage>{errors.email}</FormErrorMessage>
              </FormControl>
              <FormControl>
                <FormLabel htmlFor="type">Type of enquiry</FormLabel>
                <Select
                  id="type"
                  name="type"
                  value={values.type}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  bg={values.type ? "white" : "transparent"}
                  color={values.type ? "black" : "white"}
                >
                    <option style={style}   value="select">Select</option>
                  <option style={style}   value="freelance">Freelance project proposal</option>
                  <option style={style}  value="openSource">
                    Open source consultancy session
                  </option>
                  <option style={style}   value="hireMe">Hire for a company</option>
                  <option style={style}   value="other">Other</option>
                </Select>
              </FormControl>
              <FormControl isInvalid={touched.comment && errors.comment}>
                <FormLabel htmlFor="comment">Your message</FormLabel>
                <Textarea
                  id="comment"
                  name="comment"
                  height={250}
                  value={values.comment}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  bg={values.comment ? "white" : "#512DA8"}
                  color={values.comment ? "black" : "white"}
                />
                <FormErrorMessage>{errors.comment}</FormErrorMessage>
              </FormControl>
              <Button
                type="submit"
                colorScheme="purple"
                width="full"
                isLoading={isLoading}

              >
                Submit
              </Button>
            </VStack>
          </form>
        </Box>
      </VStack>
    </FullScreenSection>
  );
};

export default LandingSection;
