import { useState } from "react";

const useSubmit = () => {
    const [isLoading, setLoading] = useState(false);
    const [response, setResponse] = useState(null);
    const [error, setError] = useState(null); // Hata durumunu izlemek için yeni bir state ekledik

    const submit = (values) => {
      setLoading(true);
      setResponse(null);
      setError(null); // Her submit işleminden önce hatayı sıfırlayın

      // Simulating an asynchronous API request
      return new Promise((resolve, reject) => {
        setTimeout(() => {
          const randomSuccess = Math.random() < 0.5;

          if (randomSuccess) {
            setResponse({ type: "success" });
            resolve();
          } else {
            setResponse({ type: "error" });
            setError(new Error("Form submission failed")); // Hata durumunu ayarlayın
            reject(new Error("Form submission failed"));
          }

          setLoading(false);
        }, 2000); // Simulating a delay of 2 seconds
      });
    };

    return { isLoading, response, error, submit }; // Hata durumunu da döndürün
  };

  export default useSubmit;
